use crate::mysql::errcode;
use dyn_fmt::AsStrFormatExt;
use lazy_static::lazy_static;
use std::collections::HashMap;

// ErrMessage is a error message with the format specifier.
pub struct ErrMessage {
    pub raw: String,
    pub redact_arg_pos: Vec<isize>,
}

impl ErrMessage {
    // Message creates a error message with the format specifier.
    pub fn message(raw: String, redact_arg_pos: Vec<isize>) -> ErrMessage {
        ErrMessage {
            raw,
            redact_arg_pos,
        }
    }
    pub fn get_message(&self) -> String {
        if self.redact_arg_pos.len() == 0 {
            self.raw.clone()
        } else {
            self.raw.format(&self.redact_arg_pos)
        }
    }
}

